create table video(vid int unsigned not null primary key, nid int unsigned not null default 0, pid int unsigned not null, cover varchar(1024) not null default '', playlistId int unsigned not null default 0, o_playlistId int unsigned not null default 0, cid int unsigned not null default 0,  subcid int unsigned not null default 0, osubcid int unsigned not null default 0, category int unsigned not null default 0, cateCode int unsigned not null default 0, pianhua varchar(256) default '', tag varchar(1024) not null default '', tvid int unsigned not null default 0, pubdate date not null default '0000-00-00') engine=Innodb charset utf8;
